full_structure.css_content:
    * {
      margin: 0;
      padding: 0;
      box-sizing: border-box;
    }

    :root {
      --tiktok-cyan: #69C9D0;
      --tiktok-pink: #EE1D52;
      --tiktok-purple: #7C3AED;
      --background: #0a0a0a;
      --card-bg: #1a1a1a;
      --border: #2a2a2a;
      --text: #fafafa;
      --text-muted: #a1a1aa;
      --success: #22c55e;
      --warning: #eab308;
      --destructive: #ef4444;
    }

    body {
      font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
      background: var(--background);
      color: var(--text);
      line-height: 1.6;
    }

    .container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 40px 20px;
    }

    /* Header */
    .header {
      text-align: center;
      margin-bottom: 60px;
      padding: 40px;
      background: linear-gradient(135deg, var(--tiktok-pink), var(--tiktok-purple), var(--tiktok-cyan));
      border-radius: 16px;
    }

    .header h1 {
      font-size: 2.5rem;
      font-weight: 700;
      margin-bottom: 10px;
    }

    .header p {
      font-size: 1.1rem;
      opacity: 0.9;
    }

    /* Section */
    .section {
      margin-bottom: 50px;
    }

    .section-title {
      font-size: 1.5rem;
      font-weight: 600;
      margin-bottom: 24px;
      padding-bottom: 12px;
      border-bottom: 2px solid var(--tiktok-cyan);
      display: inline-block;
    }

    /* Cards Grid */
    .cards-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
      gap: 20px;
    }

    .card {
      background: var(--card-bg);
      border: 1px solid var(--border);
      border-radius: 12px;
      padding: 24px;
    }

    .metric-card {
      text-align: center;
    }

    .metric-value {
      font-size: 2rem;
      font-weight: 700;
      background: linear-gradient(135deg, var(--tiktok-cyan), var(--tiktok-pink));
      -webkit-background-clip: text;
      -webkit-text-fill-color: transparent;
      background-clip: text;
    }

    .metric-label {
      color: var(--text-muted);
      font-size: 0.9rem;
      margin-top: 8px;
    }

    .metric-trend {
      display: inline-block;
      padding: 4px 12px;
      border-radius: 20px;
      font-size: 0.8rem;
      margin-top: 10px;
    }

    .trend-up {
      background: rgba(34, 197, 94, 0.2);
      color: var(--success);
    }

    /* Table */
    .table-container {
      overflow-x: auto;
    }

    table {
      width: 100%;
      border-collapse: collapse;
      margin-top: 16px;
    }

    th, td {
      padding: 12px 16px;
      text-align: left;
      border-bottom: 1px solid var(--border);
    }

    th {
      background: var(--card-bg);
      color: var(--text-muted);
      font-weight: 500;
      font-size: 0.85rem;
    }

    /* Price Chart (CSS-based) */
    .chart-bar {
      display: flex;
      align-items: center;
      margin-bottom: 16px;
    }

    .chart-label {
      width: 100px;
      font-size: 0.9rem;
      color: var(--text-muted);
    }

    .chart-bar-container {
      flex: 1;
      height: 32px;
      background: var(--border);
      border-radius: 6px;
      overflow: hidden;
      margin: 0 12px;
    }

    .chart-bar-fill {
      height: 100%;
      border-radius: 6px;
      display: flex;
      align-items: center;
      padding-left: 12px;
      font-size: 0.85rem;
      font-weight: 500;
    }

    .chart-percent {
      width: 60px;
      text-align: right;
      font-weight: 600;
    }

    /* Case Study Cards */
    .case-card {
      border-radius: 12px;
      overflow: hidden;
    }

    .case-header {
      padding: 20px;
      color: white;
    }

    .case-header.visual { background: linear-gradient(135deg, var(--tiktok-purple), var(--tiktok-cyan)); }
    .case-header.painpoint { background: linear-gradient(135deg, var(--tiktok-cyan), var(--tiktok-pink)); }
    .case-header.authority { background: linear-gradient(135deg, var(--tiktok-pink), #dc2626); }

    .case-content {
      padding: 20px;
      background: var(--card-bg);
    }

    .case-metric {
      display: flex;
      gap: 20px;
      margin: 16px 0;
    }

    .case-metric-item {
      background: rgba(255,255,255,0.05);
      padding: 12px 16px;
      border-radius: 8px;
      text-align: center;
    }

    .case-metric-value {
      font-size: 1.2rem;
      font-weight: 600;
      color: var(--tiktok-cyan);
    }

    .case-metric-label {
      font-size: 0.75rem;
      color: var(--text-muted);
    }

    /* Badge */
    .badge {
      display: inline-block;
      padding: 4px 12px;
      border-radius: 20px;
      font-size: 0.75rem;
      font-weight: 500;
    }

    .badge-success { background: rgba(34, 197, 94, 0.2); color: var(--success); }
    .badge-warning { background: rgba(234, 179, 8, 0.2); color: var(--warning); }
    .badge-danger { background: rgba(239, 68, 68, 0.2); color: var(--destructive); }
    .badge-purple { background: rgba(124, 58, 237, 0.2); color: var(--tiktok-purple); }

    /* Alert */
    .alert {
      padding: 16px 20px;
      border-radius: 8px;
      margin-bottom: 16px;
      border-left: 4px solid;
    }

    .alert-danger {
      background: rgba(239, 68, 68, 0.1);
      border-color: var(--destructive);
    }

    .alert-warning {
      background: rgba(234, 179, 8, 0.1);
      border-color: var(--warning);
    }

    .alert-title {
      font-weight: 600;
      margin-bottom: 8px;
    }

    .alert-content {
      color: var(--text-muted);
      font-size: 0.9rem;
    }

    /* Insight Box */
    .insight-box {
      background: linear-gradient(135deg, rgba(105, 201, 208, 0.1), rgba(124, 58, 237, 0.1));
      border: 1px solid rgba(105, 201, 208, 0.3);
      border-radius: 12px;
      padding: 20px;
      margin-top: 20px;
    }

    .insight-title {
      color: var(--tiktok-cyan);
      font-weight: 600;
      margin-bottom: 8px;
    }

    /* Link Button */
    .link-btn {
      display: inline-flex;
      align-items: center;
      gap: 8px;
      padding: 10px 20px;
      background: transparent;
      border: 1px solid var(--border);
      border-radius: 8px;
      color: var(--text);
      text-decoration: none;
      font-size: 0.9rem;
      transition: all 0.2s;
      margin-top: 16px;
    }

    .link-btn:hover {
      background: var(--border);
      border-color: var(--tiktok-cyan);
    }

    /* Concentration Warning */
    .concentration-warning {
      background: linear-gradient(135deg, rgba(239, 68, 68, 0.1), rgba(234, 179, 8, 0.1));
      border: 1px solid rgba(239, 68, 68, 0.3);
      border-radius: 12px;
      padding: 24px;
      text-align: center;
      margin-top: 24px;
    }

    .concentration-value {
      font-size: 3rem;
      font-weight: 700;
      color: var(--destructive);
    }

    /* Rules Grid */
    .rules-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
      gap: 20px;
    }

    .rule-card {
      background: var(--card-bg);
      border: 1px solid var(--border);
      border-radius: 12px;
      padding: 24px;
    }

    .rule-card h4 {
      margin-bottom: 16px;
      display: flex;
      align-items: center;
      gap: 8px;
    }

    .rule-list {
      list-style: none;
    }

    .rule-list li {
      padding: 8px 0;
      color: var(--text-muted);
      border-bottom: 1px solid var(--border);
    }

	    .rule-list li:last-child {
	      border-bottom: none;
	    }

	    /* å¼•ç”¨æ¥æºé“¾æ¥ï¼ˆå†…åµŒåœ¨åŸæ–‡å¤„ï¼‰ */
	    .source-link {
	      display: inline-block;
	      margin-left: 0.35rem;
	      font-size: 0.85em;
	      color: var(--tiktok-cyan);
	      text-decoration: underline;
	    }

	    /* Footer */
	    .footer {
	      text-align: center;
	      padding: 40px;
      color: var(--text-muted);
      font-size: 0.85rem;
      border-top: 1px solid var(--border);
      margin-top: 60px;
    }

    /* Responsive */
    @media (max-width: 768px) {
      .header h1 { font-size: 1.8rem; }
      .cards-grid { grid-template-columns: 1fr; }
      .case-metric { flex-direction: column; gap: 10px; }
    }
